mapper.update:
update dish
status = #{status}, name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, update_user = #{updateUser}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select *
from dish

    and category_id = #{categoryId}
    and status = #{status}
    and name like concat('%', #{name}, '%')
